opam.opam:
opam-version: "1.2"
name: "tgls"
version: "0.8.5"
maintainer: "Daniel Bünzli <daniel.buenzl i@erratique.ch>"
authors: "Daniel Bünzli <daniel.buenzl i@erratique.ch>"
license: "ISC"
tags: ["bindings" "opengl" "opengl-es" "graphics" "org:erratique"]
homepage: "http://erratique.ch/software/tgls"
doc: "http://erratique.ch/software/tgls/doc/"
bug-reports: "https://github.com/dbuenzli/tgls/issues"
depends: [
  "ocamlfind" {build}
  "ocamlbuild" {build}
  "topkg" {build}
  "base-bytes"
  "ctypes" {>= "0.4.0"}
  "ctypes-foreign"
  "tsdl" {test}
  "result" {test}
]
available: ocaml-version >= "4.01.0"
build: ["ocaml" "pkg/pkg.ml" "build" "--pinned" "%{pinned}%"]
dev-repo: "git+http://erratique.ch/repos/tgls.git"

files.content:
--- ./extend_helper.ml
+++ ./extend_helper.ml
@@ -1,13 +1,6 @@
-(*pp cppo -V OCAML:`ocamlc -version` *)
 open Parsetree
 open Extend_protocol

-#if OCAML_VERSION < (4, 3, 0)
-# define CONST_STRING Asttypes.Const_string
-#else
-# define CONST_STRING Parsetree.Pconst_string
-#endif
-
 (** Default implementation for [Reader_def.print_outcome] using
     [Oprint] from compiler-libs *)
 let print_outcome_using_oprint ppf = function
@@ -28,7 +21,7 @@
       pstr_loc = Location.none;
       pstr_desc = Pstr_eval ({
           pexp_loc = Location.none;
-          pexp_desc = Pexp_constant (CONST_STRING (msg, None));
+          pexp_desc = Pexp_constant (Parsetree.Pconst_string (msg, None));
           pexp_attributes = [];
         }, []);
     }]
@@ -112,7 +105,7 @@
   let msg = match payload with
     | PStr [{
         pstr_desc = Pstr_eval ({
-            pexp_desc = Pexp_constant (CONST_STRING (msg, _));
+            pexp_desc = Pexp_constant (Parsetree.Pconst_string (msg, _));
           }, _);
       }] -> msg
     | _ -> "Warning: extension produced an incorrect syntax-error node"

opam.opam:
opam-version: "1.2"
name: "ctypes-foreign"
version: "0.4.0"
maintainer: "yallop@gmail.com"
authors: "yallop@gmail.com"
tags: ["org:ocamllabs" "org:mirage"]
homepage: "https://github.com/ocamllabs/ocaml-ctypes"
bug-reports: "http://github.com/ocamllabs/ocaml-ctypes/issues"
post-messages: "This package requires libffi on your system" {failure}
depexts: [
  ["libffi-dev"] {"debian"}
  ["libffi-dev"] {"ubuntu"}
  ["libffi"] {"osx" & "homebrew"}
  ["libffi"] {"osx" & "macports"}
  ["libffi-devel"] {"centos"}
  ["libffi-devel"] {"oraclelinux"}
  ["libffi-devel"] {"fedora"}
  ["libffi-dev"] {"alpine"}
  ["libffi-devel"] {"opensuse"}
]
dev-repo: "git+http://github.com/ocamllabs/ocaml-ctypes.git"

opam-version: "1.2"
name: "ctypes"
version: "0.14.0"
maintainer: "yallop@gmail.com"
authors: "yallop@gmail.com"
license: "MIT"
tags: ["org:ocamllabs" "org:mirage"]
homepage: "https://github.com/ocamllabs/ocaml-ctypes"
doc: "http://ocamllabs.github.io/ocaml-ctypes"
bug-reports: "http://github.com/ocamllabs/ocaml-ctypes/issues"
depends: [
  "base-bytes"
  "integers"
  "ocamlfind" {build}
  "conf-pkg-config" {build}
  "lwt" {test & >= "2.4.7" & < "4.0.0"}
  "ounit" {test}
  "ctypes-foreign" {test}
  "conf-ncurses" {test}
]
depopts: ["ctypes-foreign" "mirage-xen"]
available: ocaml-version >= "4.01.0"
build: [
  [make "XEN=%{mirage-xen:enable}%" "libffi.config"]
    {ctypes-foreign:installed}
  ["touch" "libffi.config"] {!ctypes-foreign:installed}
  [make "XEN=%{mirage-xen:enable}%" "ctypes-base" "ctypes-stubs"]
  [make "XEN=%{mirage-xen:enable}%" "ctypes-foreign"]
    {ctypes-foreign:installed}
]
run-test: [make "test"]
install: [make "install" "XEN=%{mirage-xen:enable}%"]
remove: ["ocamlfind" "remove" "ctypes"]
dev-repo: "git+http://github.com/ocamllabs/ocaml-ctypes.git"

